ById(“canvas”); let ctx = canvas.getContext(“2d”); // clear the canvas ctx.clearRect(0, 0, canvas.width, canvas.height); // draw the cells for (let i = 0; i < ROWS; i++) { for (let j = 0; j < COLS; j++) { for (let k = 0; k <= 3; k++) { if (drums[i][j][k]) { ctx.fillStyle = COLORS[k]; ctx.fillRect(j * CELLSIZE, k * CELLSIZE + i * CELLSIZE, CELLSIZE, CELLSIZE); } } } } // draw grid lines ctx.strokeStyle = “black”; for (let i = 0; i <= ROWS; i++) { ctx.beginPath(); ctx.moveTo(0, i * CELLSIZE); ctx.lineTo(COLS * CELLSIZE, i * CELLSIZE); ctx.stroke(); } for (let j = 0; j <= COLS; j++) { ctx.beginPath(); ctx.moveTo(j * CELLSIZE, 0); ctx.lineTo(j * CELLSIZE, ROWS * CELLSIZE); ctx.stroke(); } } // update the selected color function setColor(color) { selectedColor = color; } // handle cell clicks function handleClick(event) { let cellX = Math.floor(event.offsetX / CELLSIZE); let cellY = Math.floor(event.offsetY / CELLSIZE); for (let k = 0; k <= 3; k++) { if (drums[cellY][cellX][k] && COLORS[k] == selectedColor) { drums[cellY][cellX][k] = false; // remove cell } else if (!drums[cellY][cellX][k] && COLORS[k] == selectedColor) { drums[cellY][cellX][k] = true; // add cell } } draw(); // redraw canvas } // add event listeners function addListeners() { // handle color clicks let colorButtons = document.getElementsByClassName(“color-button”); for (let i = 0; i < colorButtons.length; i++) { colorButtons[i].addEventListener(“click”, function() { setColor(COLORS[i]); }); } // handle cell clicks let canvas = document.getElementById(“canvas”); canvas.addEventListener(“click”, handleClick); } // start the sequencer function start() { let beat = 0; setInterval(function() { // play the drums for the current beat for (let i = 0; i < ROWS; i++) { for (let j = 0; j < COLS; j++) { for (let k = 0; k <= 3; k++) { if (drums[i][j][k] && k == beat) { let audio = new Audio(“drum-” + k + “.mp3”); audio.play(); } } } } // update the beat counter beat++; if (beat > 3) { beat = 0; } }, 400); // 400 mill